import React, { useEffect } from "react";
import { useCanvas, prepareCanvas } from "./CanvasContext";

export function Canvas() {

  const { clearCanvas } = useCanvas()
  const saveImage = () => {
    console.log(canvasRef)
    var jpeg = canvasRef.current.canvas.toDataURL("image/jpeg");
    console.log(jpeg)
  }

  const {
    canvasRef,
    prepareCanvas,
    startDrawing,
    finishDrawing,
    draw,
  } = useCanvas();

  useEffect(() => {
    prepareCanvas();
  }, []);

  return (
    <div className="draw">
      <canvas
      onMouseDown={startDrawing}
      onMouseUp={finishDrawing}
      onMouseMove={draw}
      ref={canvasRef}
      />
      <button onClick={clearCanvas}>Clear</button>
      <button onClick={saveImage}>Test</button>
    </div>
  );
}
